    /**
     * 通用状态设置 (禁用、启用都是调用这个内部方法)
     * $table   数据表的名称
     * $field   需要操作的列,默认为status
     * $id      需要操作的指定id
     * 如果当前的值是1,会修改成2,如果不是1,则修改成1
     *
     * @throws \think\Exception
     * @throws \think\db\exception\DataNotFoundException
     * @throws \think\db\exception\ModelNotFoundException
     * @throws \think\exception\DbException
     * @throws \think\exception\PDOException
     */
    public function status()
    {

        if ( !editAuth() ){
            return json(array('code'=>0, 'msg'=>'测试状态下，不允许添加、修改、删除'));
        }

        //获取参数
        $table	= input('param.t');
        $field	= input('param.f', 'status');
        $id		= input('param.id/d');

        //如果缺少参数,报错
        if (empty($id) || empty($table)) {
            exit(json_encode(array('code'=>0, 'msg'=>'缺少参数,没有指定id或table')));
        }

        //保留项目
        if ($table == 'users' && $id == 1) {
            exit(json_encode(array('code'=>0, 'msg'=>'用户id为1的项目为保留项目,不能删除和修改权限')));
        }

        //查询当前状态
        $res = Db::name($table)->field($field)->find($id);

        if ($res){
            if ($res[$field] == 1){
                $status = 2;
            }else{
                $status = 1;
            }
            $res = Db::name($table)->where('id', $id)->update([$field => $status]);
            if ($res){
                exit(json_encode(array('code'=>1, 'msg'=>'修改成功')));
            }else{
                exit(json_encode(array('code'=>0, 'msg'=>'修改失败')));
            }
        }else{
            exit(json_encode(array('code'=>0, 'msg'=>'没有查到此用户')));
        }

    }



    /**
     * 通用删除 (单纯的记录删除)
     * $id      需要删除的指定id
     * $table   数据表的名称
     *
     * @throws \think\Exception
     * @throws \think\exception\PDOException
     */
    public function del()
    {

        if ( !editAuth() ){
            return json(array('code'=>0, 'msg'=>'测试状态下，不允许添加、修改、删除'));
        }

        //获取参数
        $id = input('param.id/a','');
        $table = input('param.t','');

        //如果缺少参数,报错
        if (empty($id) || empty($table)) {
            exit(json_encode(array('code'=>0, 'msg'=>'缺少参数,没有指定id或table')));
        }

        //保留项目
        if ($table == 'users' && $id == [1]) {
            exit(json_encode(array('code'=>0, 'msg'=>'用户id为1的项目为保留项目,不能删除')));
        }

        //删除指定id
        $res = Db::name($table)->delete($id);
        if ($res){
            exit(json_encode(array('code'=>1, 'msg'=>'删除成功')));
        }else{
            exit(json_encode(array('code'=>0, 'msg'=>'删除失败')));
        }
    }

}




